import React, { useState } from "react";

const Lassis = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [modalData, setModalData] = useState(null);

  const lassis = [
    {
        name: "Mango Lassi",
        image: "data:image/jpeg;base64,",
        rating: "★★★★★ 2,500 reviews",
        price: "₹100",
        discountPrice: "₹120",
        discount: "(15% off)",
      },
      {
        name: "Sweet Lassi",
        image: "data:image/jpeg;base64,",
        rating: "★★★★☆ 1,800 reviews",
        price: "₹80",
        discountPrice: "₹100",
        discount: "(20% off)",
        limitedDeal: true,
      },
      {
        name: "Salted Lassi",
        image: "data:image/jpeg;base64,",
        rating: "★★★★☆ 1,400 reviews",
        price: "₹90",
        discountPrice: "₹110",
        discount: "(18% off)",
      },
      {
        name: "Rose Lassi",
        image: "data:image/jpeg;base64,",
        rating: "★★★★☆ 1,300 reviews",
        price: "₹120",
        discountPrice: "₹150",
        discount: "(20% off)",
      },
      {
        name: "Pineapple Lassi",
        image: "data:image/jpeg;base64,",
        rating: "★★★★★ 2,100 reviews",
        price: "₹130",
        discountPrice: "₹160",
        discount: "(18% off)",
      },
  ];

  const filteredLassis = lassis.filter((lassi) =>
    lassi.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const openModal = (lassi) => {
    setModalData(lassi);
  };

  const closeModal = () => {
    setModalData(null);
  };

  return (
    <div style={{ width: "100%", height: "100%", backgroundColor: "white", margin: "0", padding: "0", display: "flex", flexDirection: "column" }}>
      {/* Search bar */}
      <div style={{ padding: "20px", textAlign: "center", flexShrink: 0 }}>
        <input
          type="text"
          placeholder="Search for a lassi..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{
            width: "60%",
            padding: "10px",
            border: "1px solid #ccc",
            borderRadius: "4px",
            fontSize: "16px",
          }}
        />
      </div>

      {/* Lassi cards */}
      <div
        className="container"
        style={{
          display: "flex",
          flexWrap: "wrap",
          justifyContent: "space-around",
          padding: "20px",
          flexGrow: 1,
          overflow: "auto",
        }}
      >
        {filteredLassis.map((lassi, index) => (
          <div
            key={index}
            className="card"
            onClick={() => openModal(lassi)}
            style={{
              width: "18rem",
              margin: "10px",
              border: "1px solid #ddd",
              borderRadius: "8px",
              overflow: "hidden",
              transition: "transform 0.3s ease",
              cursor: "pointer",
            }}
            onMouseOver={(e) => (e.currentTarget.style.transform = "scale(1.05)")}
            onMouseOut={(e) => (e.currentTarget.style.transform = "scale(1)")}
          >
            <img
              src={lassi.image}
              alt={lassi.name}
              style={{ width: "100%", height: "200px", objectFit: "cover" }}
            />
            <div className="item-name" style={{ padding: "10px" }}>
              <h5>{lassi.name}</h5>
            </div>
            <div className="rating" style={{ padding: "0 10px" }}>
              {lassi.rating}
            </div>
            {lassi.limitedDeal && (
              <div className="limited-deal" style={{ color: "red", fontWeight: "bold", padding: "0 10px" }}>
                Limited time deal
              </div>
            )}
            <div className="price" style={{ padding: "10px" }}>
              {lassi.price}{" "}
              <span className="discount" style={{ textDecoration: "line-through", color: "#888" }}>
                {lassi.discountPrice}
              </span>{" "}
              <span>{lassi.discount}</span>
            </div>
          </div>
        ))}
      </div>

      {/* Modal */}
      {modalData && (
        <div
          className="modal"
          onClick={closeModal}
          style={{
            position: "fixed",
            top: "0",
            left: "0",
            width: "100%",
            height: "100%",
            backgroundColor: "rgba(0, 0, 0, 0.7)",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <div
            className="modal-content"
            onClick={(e) => e.stopPropagation()} // Prevent closing on inner click
            style={{
              backgroundColor: "#fff",
              padding: "20px",
              borderRadius: "8px",
              textAlign: "center",
              width: "60%",
              maxWidth: "500px",
            }}
          >
            <span
              className="close-btn"
              onClick={closeModal}
              style={{
                position: "absolute",
                top: "10px",
                right: "20px",
                fontSize: "30px",
                cursor: "pointer",
              }}
            >
              &times;
            </span>
            <img
              src={modalData.image}
              alt={modalData.name}
              style={{ width: "100%", height: "250px", objectFit: "cover", marginBottom: "20px" }}
            />
            <div className="modal-item-name" style={{ fontSize: "20px", fontWeight: "bold" }}>
              {modalData.name}
            </div>
            <div className="modal-rating" style={{ margin: "10px 0" }}>
              {modalData.rating}
            </div>
            <div className="modal-price" style={{ fontSize: "18px", marginBottom: "10px" }}>
              {modalData.price}
            </div>
            <div className="modal-discount" style={{ fontSize: "16px", color: "#888" }}>
              {modalData.discountPrice}
            </div>
            <button
              className="order-now-modal"
              style={{
                padding: "10px 20px",
                fontSize: "16px",
                backgroundColor: "#28a745",
                color: "#fff",
                border: "none",
                borderRadius: "4px",
                cursor: "pointer",
                marginTop: "20px",
              }}
            >
              Add to Cart
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Lassis;
